import React from "react";
import {
  ApolloClient,
  InMemoryCache,
  ApolloProvider,
  useQuery,
  gql,
} from "@apollo/client";
import { Text, Image, View, StyleSheet, ScrollView } from "react-native";
var inventoryItem;
const client = new ApolloClient({
  //instead of a simple concatenation this uses string interpolation.
  //the uri gets a single inventory item from your store based on the variable inventoryItem
  //    uri: `https://7b1e138671fbae7fda319f305a96abd0:a7abb2368b62941c85c998491395222e@Fuse+Link.myshopify.com/admin/api/2020-01/inventory_items/${inventoryItem}.json`,

  uri: `Fuse+Link.myshopify.com/admin/api/2020-01/graphql.json`,

  cache: new InMemoryCache(),
});

const ExchangeRates = () => {
  //this query looks for the feild of rates in the json object
  const { loading, error, data } = useQuery(gql`
    {
      shop {
        products(first: 5) {
          edges {
            node {
              id
              handle
            }
          }
          pageInfo {
            hasNextPage
          }
        }
      }
    }
  `);
  console.log(data);
  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error :(</p>;

  return data.shop.map(({ products, id }) => (
    <Text key={id}>
      {products}
    </Text>
  ));
};

const Client = () => {
  return (
    <ApolloProvider client={client}>
      <ScrollView>
        <Text>My first Apollo app ðŸš€</Text>
      </ScrollView>
      <ExchangeRates />
    </ApolloProvider>
  );
};
export default Client;
