import React, { useState, useEffect } from "react";
import {
  Text,
  View,
  StyleSheet,
  Button,
  Image,
  TouchableOpacity,
  Dimensions,
  Alert,
} from "react-native";
import { BarCodeScanner } from "expo-barcode-scanner";
import axios from "axios";

const Scanner = () => {
  const [hasPermission, setHasPermission] = useState(null);
  const [scanned, setScanned] = useState(false);
  const [listData, setListData] = useState([]);

  useEffect(() => {
    (async () => {
      const { status } = await BarCodeScanner.requestPermissionsAsync();
      setHasPermission(status === "granted");
    })();
  }, []);

  //   const url = "http://192.168.1.224:4000/graphql";

  const handleBarCodeScanned = async ({ type, data }) => {
    setScanned(true);
    setListData(data);

    //alert(`Bar code with type ${type} and data ${data} has been scanned!`);
    // return fetch(
    //   "https://d47831e4e5086f0352adceeaa698d093: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@idk.myshopify.com/admin/api/2020-01/orders.json"
    // )
    //   .then((response) => response.json())
    //   .then((listData) => {
    //     setListData({ listData });
    //   })
    //   .catch((error) => {
    //     console.error(error);
    //   });
  };

  if (hasPermission === null) {
    return <Text>Requesting for camera permission</Text>;
  }
  if (hasPermission === false) {
    return <Text>No access to camera</Text>;
  }

  return (
    <View style={styles.cameraContainer}>
      <BarCodeScanner
        onBarCodeScanned={scanned ? undefined : handleBarCodeScanned}
        style={StyleSheet.absoluteFill}
      />
      <Image
        style={styles.barcode}
        source={require("../assets/images/barcode2.png")}
      />
      <Text style={styles.txt}>{listData}</Text>
      {scanned && (
        <Button
          style={styles.btn}
          title={"Scan Again?"}
          onPress={() => setScanned(false)}
        />
      )}
    </View>
  );
};
const { width } = Dimensions.get("screen");
const qrSize = width * 0.5;
const styles = StyleSheet.create({
  cameraContainer: {
    flex: 1,
    flexDirection: "column",
    justifyContent: "flex-end",
    alignItems: "center",
  },
  barcode: {
    marginBottom: "70%",
    width: qrSize * 1.5,
    height: qrSize,
  },
  txt: {
    fontSize: width * 0.06,
    marginTop: "10%",
    textAlign: "center",
    width: "70%",
    color: "white",
  },
  btn: {
    backgroundColor: "#80e5ff",
  },
});

export default Scanner;
