import React from "react";
import {
  ApolloClient,
  InMemoryCache,
  ApolloProvider,
  useQuery,
  gql,
} from "@apollo/client";
import { Text, Image, View, StyleSheet, ScrollView } from "react-native";
var inventoryItem;
const client = new ApolloClient({
  //instead of a simple concatenation this uses string interpolation.
  //the uri gets a single inventory item from your store based on the variable inventoryItem
  //    uri: `https://7b1e138671fbae7fda319f305a96abd0:a7abb2368b62941c85c998491395222e@Fuse+Link.myshopify.com/admin/api/2020-01/inventory_items/${inventoryItem}.json`,
  async: true,
  crossDomain: true,
  fetchOptions: {
    mode: "no-cors",
  },
  uri: 'https://48p1r2roz4.sse.codesandbox.io',

  //uri: `https://fuse-dev.myshopify.com/api/2020-01/graphql.json`,
  //uri: "http://swapi.dev/api/planets/1/",

  headers: {
    "Access-Control-Allow-Credentials": true,
    "Access-Control-Allow-Origin": "http://swapi.dev/api/planets/1/",
    "Content-Type": "application/graphql",
    "X-Shopify-Storefront-Access-Token": "fab5f8cc3552d5f0590dc14f42aa5214",
    "X-Shopify-Access-Token:": "a7abb2368b62941c85c998491395222e",
    Host: "bar.other",
    Accept: "text/html,application/xhtml+xml,application/xml;q=0.9,*/*;q=0.8",
    "Accept-Language": "en-us,en;q=0.5",
    "Accept-Encoding": "gzip,deflate",
    "Accept-Charset": "ISO-8859-1,utf-8;q=0.7,*;q=0.7",
    Connection: "keep-alive",
    Origin: "http://192.168.1.224:19006/",
    "Access-Control-Request-Method": "POST",
    "Access-Control-Request-Headers": "X-PINGOTHER, Content-Type",
  },
  cache: new InMemoryCache(),
});

const APIData = () => {
  //this query looks for the feild of rates in the json object
  const SHOP_DETAILS = gql`
  query GetExchangeRates {
    rates(currency: "USD") {
      currency
      rate
    }
  }
`;
  const { loading, error, data } = useQuery(SHOP_DETAILS);
  
  if (loading) return <Text>Loading...</Text>;
  if (error) return <Text>Error :(</Text>;
  console.log(data);
  return data.shop.map(({ id, name }) => (
    <Text key={currency}>{currency}: {rate}</Text>
  ));
  // return data.shop.map(({ id, name }) => (
  //   <Text key={id}>{name}</Text>
  // ));
};

const Client = () => {
  return (
    <ApolloProvider client={client}>
      <View style={styles.scroll}>
        <ScrollView>
          <Text>Product Retreived ðŸš€</Text>
        </ScrollView>

        <APIData />
      </View>
    </ApolloProvider>
  );
};

const styles = StyleSheet.create({
  scroll: {},
});

export default Client;
